// QuizControl.tsx

import React, { useEffect, useState } from 'react';
import '../stylesheet/QuizControl.css'
import '../stylesheet/style.css';
import { handleTextboxChange } from '../components/ThreeScene';
import CountryInfo from './CountryInfo';
import { Timer } from '../utils/Timer';

const QuizControl: React.FC <{gameMode:string}>= ({gameMode}) => {
  const [isQuizStarted, setIsQuizStarted] = useState(false);
  console.log("Making the timer");
  const [gameTimer, setGameTimer] = useState<Timer | null>(null);


  setTimeout(() => {
    const timer = new Timer();
    setGameTimer(timer);

    // Clean up the timer when the component unmounts
    return () => {
      timer.stop();
    };
  }, 1000);

  useEffect(() => {
    if (gameTimer) {
      if (isQuizStarted) {
        gameTimer.start();
      } else {
        gameTimer.stop();
      }
    }
  }, [isQuizStarted, gameTimer]);

  const handleStart = () => {
    setIsQuizStarted(true);
  };

  const handlePause = () => {
    setIsQuizStarted(false);
    // Add any other logic needed when pausing the quiz
  };
  console.log("Returning QuizControl");

  return (
    <div id="quiz-controls">
      <div id="quiz-controls-table">
        <div id='timer-cell'>
          <div id="timer">00:00:00</div>
          <button className="button" id="give-up-btn">Give Up</button>
        </div>
        {/* <div className='quiz-actions'>
          {isQuizStarted ? (
            <button className="button" id="pause-btn" onClick={handlePause}>Start</button>
          ) : (
            <button className="button" id="start-btn" onClick={handleStart}>Pause</button>
          )}
        </div> */}
        <div id='answer-box-container'>
          <label id="answer-box-text" htmlFor="textbox" >Enter Country's Name&nbsp;:&nbsp;</label>
          {/* <input type="text" id="answer-box-input" name="textbox" 
          onInput={(event) => handleTextboxChange(event, gameTimer)} autoComplete='off' autoCorrect='off'></input> */}
          <div id="guessed-line">
            <div id="country-counter">0</div><div>&nbsp;/&nbsp;191 guessed</div>
          </div>
        </div>
      </div>
      
      <CountryInfo/>
    </div>
  );
};
//! MOVE FOLLOW COUNTRY TO THE RIGHT OF ENTER COUNTRY INFO
export default QuizControl;
