// QuizControl.tsx

import React, { useState } from 'react';
import '../stylesheet/QuizControl.css'
import '../stylesheet/style.css';
import { handleTextboxChange } from '../components/ThreeScene';

const QuizControl: React.FC<{ showEntryPoint: boolean }> = ({ showEntryPoint }) => {
  const [isQuizStarted, setIsQuizStarted] = useState(false);

  const handleStart = () => {
    setIsQuizStarted(true);
    // Add any other logic needed when starting the quiz
  };

  const handlePause = () => {
    setIsQuizStarted(false);
    // Add any other logic needed when pausing the quiz
  };
  return (
    <div id="quiz-controls" style={{ display: showEntryPoint ? 'block' : 'none' }}>
      <div id="quiz-controls-table">
        <div id='timer-cell'>
          <div id="timer">00:00:00</div>
          <button className="button" id="give-up-btn">Give Up</button>
        </div>
        <div className='quiz-actions'>
          {!isQuizStarted ? (
            <button className="button" id="start-btn" onClick={handleStart}>Start</button>
          ) : (
            <button className="button" id="pause-btn" onClick={handlePause}>Pause</button>
          )}
        </div>
        <div id='answer-box-container'>
          <label id="answer-box-text" htmlFor="textbox" >Enter Country's Name&nbsp;:&nbsp;</label>
          <input type="text" id="answer-box-input" name="textbox" onInput={handleTextboxChange} autoComplete='off' autoCorrect='off'></input>
          <div id="guessed-line">
            <div id="country-counter">0</div><div>&nbsp;/&nbsp;191 guessed</div>
          </div>
        </div>
      </div>
    </div>
  );
};
//! MOVE FOLLOW COUNTRY TO THE RIGHT OF ENTER COUNTRY INFO
//!! MAKE START AND PAUSE CHANGE TO THE OTHER WHEN ONE IS PRESSED
export default QuizControl;
